mapper.update:
		UPDATE STORE SET   sReplycnt = sReplycnt +1 ,
               sScore = sScore + #{srscore}
               WHERE SNo = #{sno}

mapper.select:
	select * from
    (select rownum RN, A.* from
    (select * from storereview where sno = #{sno} 
    order by srno desc)A)
    where RN BETWEEN #{startRow} and #{endRow}
